INSERT INTO `tbl_noidungtin` (`MaBaiViet`, `MaChuDe`, `MaNguoiDung`, `TieuDe`, `TomTat`, `NoiDung`, `NgayDang`, `HinhAnh`, `ChuThichAnh`, `LuotXem`, `KiemDuyet`) VALUES
(28, 1, 1, 'Chưa \'chốt\' đại học nào tuyển thí sinh đạt 8 điểm Toán vào ngành bán dẫn', 'Yêu cầu thí sinh vào ngành bán dẫn phải đạt 8 điểm Toán trở lên không áp dụng với tất cả trường, danh sách chưa được Bộ Giáo dục công bố.', '<p>&Ocirc;ng Nguyễn Anh Dũng, Ph&oacute; vụ trưởng Vụ Gi&aacute;o dục đại học, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, ng&agrave;y 21/5 trả lời&nbsp;<em>VnExpress</em>&nbsp;như tr&ecirc;n.</p>\r\n\r\n<p>Theo chuẩn chương tr&igrave;nh đ&agrave;o tạo về vi mạch b&aacute;n dẫn do Bộ ban h&agrave;nh tuần trước, th&iacute; sinh muốn học hệ cử nh&acirc;n, kỹ sư c&aacute;c ng&agrave;nh về vi mạch b&aacute;n dẫn, nếu đăng k&yacute; x&eacute;t bằng điểm thi tốt nghiệp THPT, phải sử dụng tổ hợp c&oacute; m&ocirc;n To&aacute;n v&agrave; &iacute;t nhất một m&ocirc;n Khoa học tự nhi&ecirc;n ph&ugrave; hợp.</p>\r\n\r\n<p>Tổng điểm c&aacute;c m&ocirc;n phải đạt tối thiểu 80% thang điểm x&eacute;t, v&iacute; dụ 24/30 với tổ hợp ba m&ocirc;n. Trong đ&oacute;, điểm b&agrave;i thi m&ocirc;n To&aacute;n đạt &iacute;t nhất 80% thang điểm, tương đương 8/10.</p>\r\n\r\n<p>Nếu d&ugrave;ng phương thức x&eacute;t tuyển kh&aacute;c, điểm tr&uacute;ng tuyển quy đổi phải tương đương.</p>', '2025-05-21 23:23:51', 'images/1747844631_gd01.jpg', 'Phòng thí nghiệm phục vụ đào tạo và nghiên cứu về bán dẫn của trường Đại học Khoa học Tự nhiên, Đại học Quốc gia Hà Nội. Ảnh: Giang Huy', 2, 1),
(29, 2, 8, 'Đổi mới sáng tạo trong chuỗi giá trị ngành công nghiệp nhiệt điện LNG', 'Trung tâm Đổi mới sáng tạo Quốc gia phối hợp Tập đoàn SK tổ chức Hội thảo Đổi mới sáng tạo trong chuỗi giá trị ngành công nghiệp nhiệt điện khí thiên nhiên hóa lỏng.', '<p>Sự kiện được tổ chức ng&agrave;y 21/5 trong khu&ocirc;n khổ Chương tr&igrave;nh &ldquo;Hợp t&aacute;c chia sẻ giải ph&aacute;p cho c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp tương lai&rdquo;.</p>\r\n\r\n<p>Ph&aacute;t biểu tại chương tr&igrave;nh, &ocirc;ng Đỗ Tiến Thịnh, Ph&oacute; Gi&aacute;m đốc Trung t&acirc;m Đổi mới s&aacute;ng tạo Quốc gia (NIC - trực thuộc Bộ T&agrave;i ch&iacute;nh) nhận định:&nbsp;<em>&quot;Trong bối cảnh Việt Nam đối mặt với nhu cầu ti&ecirc;u thụ điện ng&agrave;y c&agrave;ng tăng cao v&agrave; mục ti&ecirc;u giảm ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh theo cam kết ph&aacute;t thải r&ograve;ng bằng 0 v&agrave;o năm 2050, kh&iacute; thi&ecirc;n nhi&ecirc;n h&oacute;a lỏng (LNG) đang được định hướng l&agrave; nguồn năng lượng chuyển tiếp quan trọng.</em></p>\r\n\r\n<p><em>Hội thảo g&oacute;p phần l&agrave;m r&otilde; vai tr&ograve; của LNG trong Quy hoạch điện VIII, giới thiệu c&aacute;c giải ph&aacute;p c&ocirc;ng nghệ ti&ecirc;n tiến trong ng&agrave;nh c&ocirc;ng nghiệp LNG, kết nối doanh nghiệp Việt Nam - H&agrave;n Quốc trong chuỗi gi&aacute; trị LNG, đồng thời thảo luận về c&aacute;c th&ocirc;ng lệ quốc tế trong đầu tư v&agrave; m&ocirc; h&igrave;nh hợp đồng, hướng tới ph&aacute;t triển bền vững v&agrave; hiệu quả lĩnh vực n&agrave;y tại Việt Nam&quot;</em>, &ocirc;ng Đỗ Tiến Thịnh khẳng định.</p>\r\n', '2025-05-21 23:27:20', 'images/1747844840_khcn01.jpg', 'LNG đang được định hướng là nguồn năng lượng chuyển tiếp quan trọng.', 3, 1),
(31, 35, 8, 'Bệnh tật giày vò, nghèo đói bủa vây đôi vợ chồng khốn khó', 'Mỗi ngày trôi qua, ông Ngọn và bà Tiềm vẫn nương tựa vào nhau để vượt qua những khốn khó, cơ cực. Đói nghèo, bệnh tật không chỉ bào mòn sức lực mà còn lấy dần những hy vọng của họ.', '<p><strong>Ph&oacute; mặc bệnh tật v&igrave; kh&ocirc;ng c&oacute; tiền</strong></p>\r\n\r\n<p>Cuộc sống của vợ chồng &ocirc;ng N&ocirc;ng Văn Ngọn (SN 1977) v&agrave; b&agrave; Ma Thị Tiềm (SN 1973) ở th&ocirc;n N&agrave; Khanh, x&atilde; Đổng X&aacute;, huyện Na R&igrave;, tỉnh Bắc Kạn, từ l&acirc;u đ&atilde; gắn liền với c&aacute;i ngh&egrave;o, c&aacute;i khổ v&agrave; bệnh tật triền mi&ecirc;n.&nbsp;</p>\r\n\r\n<p>Nh&agrave; ngh&egrave;o n&ecirc;n mỗi lần ốm đau, &ocirc;ng b&agrave; chỉ biết chịu đựng chứ kh&ocirc;ng d&aacute;m đi bệnh viện. Cũng v&igrave; kh&ocirc;ng được thăm kh&aacute;m v&agrave; điều trị kịp thời m&agrave; &ocirc;ng Ngọn dần trở th&agrave;nh người khuyết tật.</p>\r\n', '2025-05-22 11:11:04', '../images/1747887064_na1.jpg', 'Không có tiền chữa bệnh, ông Ngọn đã bị hỏng một mắt (Ảnh: Quang Hưng).', 1, 1),
(35, 1, 1, 'Thảo cầm viên tung loạt trải nghiệm hè cho trẻ rèn kỹ năng, tránh xa điện thoại', 'Hè này, Thảo cầm viên tổ chức hơn 20 hoạt động trải nghiệm giúp các em thiếu nhi rèn luyện kỹ năng trong môi trường thiên nhiên trong lành, hướng đến hạn chế sử dụng các thiết bị điện tử như tivi, điện thoại.', '<p>Trong hơn 20 hoạt động vui h&egrave; ở&nbsp;<a href=\"https://tuoitre.vn/thao-cam-vien.html\" title=\"Thảo cầm viên\">Thảo cầm vi&ecirc;n</a>&nbsp;TP.HCM, nổi bật c&oacute; chuỗi kể chuyện về c&aacute;c &quot;idol&quot; vườn th&uacute; đ&atilde; &quot;l&agrave;m mưa l&agrave;m gi&oacute;&quot; mạng x&atilde; hội thời gian qua.</p>\r\n\r\n<p>Du kh&aacute;ch đến Thảo cầm vi&ecirc;n sẽ được nghe những giai thoại th&uacute; vị về c&aacute;c &quot;idol&quot; vườn th&uacute; như chuyện&nbsp;<a href=\"https://tuoitre.vn/ly-ky-gau-meo-di-chui-35-ngay-trong-container-lanh-18-do-tu-my-ve-thao-cam-vien-sai-gon-20241130104251636.htm\" title=\"&quot;đi chui&quot; container\">&quot;đi chui&quot; container</a>&nbsp;chở thịt heo từ Mỹ về Việt Nam của c&ocirc; gấu m&egrave;o C&ocirc;ng Thị Tai Nơ, chuyện c&ocirc; gấu Noel tr&ocirc;ng mạnh mẽ nhưng th&iacute;ch được &ocirc;m v&agrave; uống mật ong...</p>\r\n\r\n<p>Đại diện Thảo cầm vi&ecirc;n cho biết những c&acirc;u chuyện n&agrave;y thu h&uacute;t đ&ocirc;ng đảo sự quan t&acirc;m của người d&acirc;n v&agrave; đ&atilde; g&oacute;p phần tạo n&ecirc;n thương hiệu của vườn th&uacute;.</p>\r\n\r\n<p>Tận dụng lợi thế đ&oacute;, Thảo cầm vi&ecirc;n mong muốn mở ra chương tr&igrave;nh &quot;Nh&acirc;n vi&ecirc;n vườn th&uacute; kể chuyện&quot;, kể về th&oacute;i quen sinh hoạt của c&aacute;c &quot;idol&quot; n&agrave;y. Đồng thời lồng gh&eacute;p giới thiệu về đặc t&iacute;nh chung của lo&agrave;i. Từ đ&oacute; lan tỏa th&ocirc;ng điệp về bảo tồn động vật hoang d&atilde; đến du kh&aacute;ch, đặc biệt l&agrave; c&aacute;c em thiếu nhi.</p>\r\n\r\n<p>Ngo&agrave;i ra Thảo cầm vi&ecirc;n cũng ch&uacute; trọng mở ra những trải nghiệm gi&uacute;p c&aacute;c em thiếu nhi r&egrave;n luyện kỹ năng trong m&ocirc;i trường thi&ecirc;n nhi&ecirc;n trong l&agrave;nh, hướng đến hạn chế sử dụng thiết bị điện tử.</p>\r\n\r\n<p>C&aacute;c em nhỏ c&oacute; thể đến học vẽ c&ugrave;ng c&aacute;c họa sĩ c&oacute; kinh nghiệm giảng dạy mỹ thuật cho thiếu nhi.</p>\r\n\r\n<p>Phụ huynh cũng c&oacute; thể c&ugrave;ng con đọc s&aacute;ch miễn ph&iacute; với h&agrave;ng trăm đầu s&aacute;ch đa dạng từ truyện cổ t&iacute;ch, s&aacute;ch kỹ năng đến t&aacute;c phẩm văn học nổi tiếng.</p>\r\n\r\n<p>Thảo cầm vi&ecirc;n cũng sẽ mở rộng vườn th&uacute; th&acirc;n thiện, cho ph&eacute;p c&aacute;c em nhỏ trực tiếp giao lưu với những lo&agrave;i vật th&acirc;n thiện như cầy meerkat, s&oacute;c Mỹ, bọ &uacute;...</p>\r\n\r\n<p>Đặc biệt từ ng&agrave;y 27-5 đến 1-6, Thảo cầm vi&ecirc;n tổ chức chương tr&igrave;nh &quot;Mừng Tết Đoan Ngọ - Vui tết thiếu nhi&quot; với nhiều hoạt động t&igrave;m hiểu văn h&oacute;a, ẩm thực như vẽ n&oacute;n l&aacute;, l&agrave;m b&aacute;nh truyền thống, l&agrave;m gốm, c&ugrave;ng b&eacute; nhận biết tr&aacute;i c&acirc;y m&ugrave;a h&egrave;... Miễn ph&iacute; v&eacute; v&agrave;o cổng sau 17h.</p>\r\n\r\n<p>Với vai tr&ograve; l&agrave; 1 trong 8 vườn th&uacute; cổ của thế giới, Thảo cầm vi&ecirc;n kh&ocirc;ng chỉ l&agrave; điểm đến vui chơi hấp dẫn của du kh&aacute;ch m&agrave; c&ograve;n l&agrave; nơi gi&aacute;o dục c&aacute;c thế hệ về tầm quan trọng của việc bảo tồn thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-23 00:36:26', '../images/1747935386_gd02.jpg', 'Thảo cầm viên sắp có chương trình kể chuyện về các con vật \"làm mưa làm gió\" trên mạng xã hội thời gian qua - Ảnh: BÙI NHI', 0, 1),
(36, 1, 1, 'UBND TP.HCM chỉ đạo tháo gỡ vướng mắc về thành lập trường tư thục', 'UBND TP.HCM vừa có văn bản về việc tháo gỡ các vướng mắc liên quan điều kiện thành lập trường tư thục trên địa bàn TP.HCM.', '<p>Chủ tịch UBND TP.HCM chỉ đạo giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo chủ tr&igrave;, phối hợp c&aacute;c sở, ng&agrave;nh, địa phương c&oacute; li&ecirc;n quan trong qu&aacute; tr&igrave;nh thẩm định hồ sơ đề nghị th&agrave;nh lập trường ngo&agrave;i c&ocirc;ng lập,&nbsp;<a href=\"https://tuoitre.vn/truong-tu-thuc-dau-tien-o-ha-noi-cong-bo-diem-chuan-vao-lop-10-20240701172443666.htm\" title=\"trường tư thục\">trường tư thục</a>&nbsp;tr&ecirc;n địa b&agrave;n TP.HCM khi c&oacute; c&aacute;c vướng mắc li&ecirc;n quan đến quy hoạch, đất đai trong việc th&agrave;nh lập c&aacute;c trường ngo&agrave;i c&ocirc;ng lập, được xem x&eacute;t, vận dụng giải quyết như sau:</p>\r\n\r\n<p>Thứ nhất, cho ph&eacute;p nh&agrave; đầu tư x&acirc;y dựng mới c&ocirc;ng tr&igrave;nh trường học, cải tạo n&acirc;ng cấp c&ocirc;ng tr&igrave;nh hiện hữu th&agrave;nh trường học tại vị tr&iacute; đất c&oacute; mục đ&iacute;ch sử dụng l&agrave;m nh&agrave; ở hoặc mục đ&iacute;ch sử dụng đất kh&aacute;c nhưng kh&ocirc;ng thuộc trường hợp chuyển mục đ&iacute;ch sử dụng đất theo quy định tại khoản 1 điều 121 Luật Đất đai năm 2024 v&agrave; tạm thời chưa &quot;x&acirc;y dựng phương &aacute;n sử dụng đất tr&igrave;nh cấp c&oacute; thẩm quyền ph&ecirc; duyệt&quot; (chưa xem x&eacute;t đề xuất đối với c&aacute;c trường hợp li&ecirc;n quan đến t&agrave;i sản c&ocirc;ng, đất c&ocirc;ng).</p>\r\n\r\n<p>Thời hạn &aacute;p dụng nội dung th&aacute;o gỡ n&ecirc;u tr&ecirc;n thực hiện từ khi ban h&agrave;nh c&ocirc;ng văn n&agrave;y đến hết năm 2025 (chỉ xem x&eacute;t giải quyết những trường hợp được Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo ho&agrave;n th&agrave;nh thẩm định, tr&igrave;nh trước ng&agrave;y 31-12-2025).</p>\r\n\r\n<p>Sau thời hạn n&ecirc;u tr&ecirc;n, giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đ&aacute;nh gi&aacute; hiệu quả, b&aacute;m s&aacute;t v&agrave;o c&aacute;c nội dung điều chỉnh của th&ocirc;ng tư, nghị định, sắp xếp bộ m&aacute;y, c&aacute;c chỉ ti&ecirc;u, đề xuất UBND TP để c&oacute; giải ph&aacute;p ph&ugrave; hợp, đồng bộ.</p>\r\n\r\n<p>Thứ hai, giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo chủ tr&igrave;, tham mưu giải quyết đối với từng trường hợp; t&ugrave;y theo c&aacute;c nội dung vướng mắc để đề xuất giải ph&aacute;p, hướng th&aacute;o gỡ cụ thể đối với từng hồ sơ đề xuất th&agrave;nh lập trường học tư thục.</p>\r\n\r\n<p>Trường hợp đ&atilde; phối hợp thực hiện c&aacute;c y&ecirc;u cầu, đề xuất v&agrave; c&oacute; sự đồng thuận, ủng hộ chung của địa phương nơi trường đặt trụ sở (hoặc c&aacute;c điểm trường) v&agrave; c&aacute;c sở, ng&agrave;nh li&ecirc;n quan (Sở N&ocirc;ng nghiệp v&agrave; M&ocirc;i trường, Sở X&acirc;y dựng, Cảnh s&aacute;t ph&ograve;ng ch&aacute;y v&agrave; chữa ch&aacute;y TP&hellip;) th&igrave; xem x&eacute;t, tổng hợp tr&igrave;nh chủ tịch UBND TP quyết định th&agrave;nh lập.</p>\r\n\r\n<p>Trong văn bản, UBND TP.HCM chỉ đạo một số lưu &yacute; trong qu&aacute; tr&igrave;nh triển khai thực hiện v&agrave; tham mưu.</p>\r\n\r\n<p>Đầu ti&ecirc;n, c&aacute;c quy định về ti&ecirc;u ch&iacute; ph&ugrave; hợp, đảm bảo c&aacute;c yếu tố về t&iacute;nh kh&aacute;ch quan, tiến độ, thời hạn, hiệu quả&hellip; khi cụ thể h&oacute;a chủ trương thực hiện.</p>\r\n\r\n<p>Kế đ&oacute;, đảm bảo c&ocirc;ng t&aacute;c phối hợp, sự thống nhất &yacute; kiến của c&aacute;c đơn vị, địa phương li&ecirc;n quan đối với c&aacute;c trường hợp cụ thể. Đồng thời chủ động theo d&otilde;i, cập nhật c&aacute;c quy định ph&aacute;p luật li&ecirc;n quan, kịp thời tham mưu cấp c&oacute; thẩm quyền xem x&eacute;t, chỉ đạo.</p>\r\n\r\n<p>UBND TP.HCM y&ecirc;u cầu Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo lưu &yacute; mỗi trường hợp nh&agrave; đầu tư đề xuất th&agrave;nh lập trường được thẩm định th&ocirc;ng qua một tờ tr&igrave;nh; hồ sơ tr&igrave;nh cần đảm bảo đủ th&agrave;nh phần, c&oacute; ch&iacute;nh kiến cụ thể với vai tr&ograve; l&agrave; cơ quan chủ tr&igrave; tổng hợp tham mưu tr&igrave;nh, đ&iacute;nh k&egrave;m dự thảo quyết định th&agrave;nh lập hoặc dự thảo văn bản của UBND TP ph&uacute;c đ&aacute;p nh&agrave; đầu tư.</p>\r\n\r\n<p>Đối với c&aacute;c hồ sơ Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đ&atilde; tr&igrave;nh UBND TP tại tờ tr&igrave;nh số 424/TTr-SGDĐT ng&agrave;y 3-2-2025 v&agrave; tờ tr&igrave;nh 849/TTr-SGDĐT ng&agrave;y 25-2-2025, UBND TP giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo khẩn trương hướng dẫn nh&agrave; đầu tư ho&agrave;n chỉnh đề &aacute;n, tr&igrave;nh UBND TP trước ng&agrave;y 25-5-2025 để xem x&eacute;t, quyết định.</p>\r\n\r\n<p>Đối với c&aacute;c hồ sơ nh&agrave; đầu tư đ&atilde; nộp v&agrave; được Văn ph&ograve;ng UBND TP chuyển hoặc đ&atilde; nộp tại Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đang xử l&yacute;; hoặc đ&atilde; v&agrave; đang triển khai thủ tục đề xuất th&agrave;nh lập trường, giao Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo: xem x&eacute;t, c&oacute; văn bản hướng dẫn cụ thể trước ng&agrave;y 30-6-2025 cho c&aacute;c nh&agrave; đầu tư để phối hợp thực hiện.</p>\r\n\r\n<p>Tr&ecirc;n cơ sở đ&oacute;, chủ tr&igrave; c&oacute; văn bản lấy &yacute; kiến từng sở, ng&agrave;nh, địa phương đối với từng nội dung c&oacute; li&ecirc;n quan (trừ trường hợp nh&agrave; đầu tư đ&atilde; lấy &yacute; kiến của c&aacute;c sở, ng&agrave;nh c&oacute; li&ecirc;n quan th&igrave; c&oacute; thể sử dụng để thẩm định), từ đấy tổng hợp kết quả, tham mưu tr&igrave;nh UBND TP theo quy định.</p>\r\n', '2025-05-23 00:40:45', '../images/1747935645_gd03.jpg', 'Học sinh một trường tư thục ở TP.HCM - Ảnh: TRỌNG NHÂN', 1, 1),
(37, 1, 1, 'Đại học Quốc gia Hà Nội tuyển 300 học sinh, sinh viên bồi dưỡng thành giảng viên, nhà khoa học2', 'Đại học Quốc gia Hà Nội yêu cầu ứng viên tham gia tuyển chọn là học sinh, sinh viên có kết quả học tập xuất sắc, có tài năng và đam mê giảng dạy, nghiên cứu khoa học.', '<p><a href=\"https://tuoitre.vn/dai-hoc-quoc-gia-ha-noi.html\" target=\"_blank\" title=\"Đại học Quốc gia Hà Nội\">Đại học Quốc gia H&agrave; Nội</a>&nbsp;vừa ph&ecirc; duyệt chương tr&igrave;nh ph&aacute;t triển nguồn giảng vi&ecirc;n, nh&agrave; khoa học Đại học Quốc gia H&agrave; Nội giai đoạn 2026-2031.</p>\r\n\r\n<p>Chương tr&igrave;nh thực hiện với mục ti&ecirc;u ph&aacute;t triển đội ngũ giảng vi&ecirc;n, nh&agrave; khoa học kế cận c&oacute; tr&igrave;nh độ cao, được đ&agrave;o tạo b&agrave;i bản trong v&agrave; ngo&agrave;i nước, đ&aacute;p ứng ti&ecirc;u chuẩn hội nhập, l&agrave;m n&ograve;ng cốt cho c&aacute;c lĩnh vực khoa học cơ bản, khoa học c&ocirc;ng nghệ, lĩnh vực ưu ti&ecirc;n chiến lược của Đại học Quốc gia H&agrave; Nội.</p>\r\n\r\n<p>Mục ti&ecirc;u cụ thể: tuyển chọn khoảng 300 ứng vi&ecirc;n để đ&agrave;o tạo, bồi dưỡng trong v&agrave; ngo&agrave;i nước theo lộ tr&igrave;nh trở th&agrave;nh giảng vi&ecirc;n, nh&agrave; khoa học chất lượng cao của Đại học Quốc gia H&agrave; Nội trong tương lai.</p>\r\n\r\n<p>Ứng vi&ecirc;n tham gia dự tuyển l&agrave; học sinh, sinh vi&ecirc;n c&oacute; kết quả học tập xuất sắc, c&oacute; t&agrave;i năng v&agrave; đam m&ecirc; giảng dạy,&nbsp;<a href=\"https://tuoitre.vn/nghien-cuu-khoa-hoc.html\" target=\"_blank\" title=\"nghiên cứu khoa học\">nghi&ecirc;n cứu khoa học</a>.</p>\r\n\r\n<p>Đại học Quốc gia H&agrave; Nội ưu ti&ecirc;n học sinh, sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn, c&oacute; tinh thần vượt kh&oacute; v&agrave; c&oacute; nguyện vọng trở th&agrave;nh giảng vi&ecirc;n, nh&agrave; khoa học.</p>\r\n\r\n<p>C&aacute;c ứng vi&ecirc;n tham gia chương tr&igrave;nh ph&aacute;t triển nguồn giảng vi&ecirc;n, nh&agrave; khoa học sẽ được k&yacute; hợp đồng đ&agrave;o tạo giảng vi&ecirc;n, nghi&ecirc;n cứu vi&ecirc;n nguồn/hợp đồng lao động của Đại học Quốc gia H&agrave; Nội v&agrave; được gửi đi đ&agrave;o tạo tại c&aacute;c đối t&aacute;c quốc tế của Đại học Quốc gia H&agrave; Nội.</p>\r\n\r\n<p>Đồng thời được t&agrave;i trợ một phần hoặc to&agrave;n bộ học ph&iacute;, sinh hoạt ph&iacute;, chi ph&iacute; đi lại, thực tập, học tập v&agrave; nghi&ecirc;n cứu trong v&agrave; ngo&agrave;i nước; được hỗ trợ học ngoại ngữ, hỗ trợ chỗ ở trong thời gian học tập, nghi&ecirc;n cứu tại Đại học Quốc gia H&agrave; Nội hoặc tại c&aacute;c cơ sở đ&agrave;o tạo, nghi&ecirc;n cứu, đối t&aacute;c của Đại học Quốc gia H&agrave; Nội ở trong v&agrave; ngo&agrave;i nước...</p>\r\n\r\n<p>Sau khi ho&agrave;n th&agrave;nh chương tr&igrave;nh v&agrave; đ&aacute;p ứng c&aacute;c ti&ecirc;u chuẩn theo quy định, ứng vi&ecirc;n được tuyển dụng ch&iacute;nh thức v&agrave;o vị tr&iacute; giảng vi&ecirc;n, nghi&ecirc;n cứu vi&ecirc;n tại c&aacute;c đơn vị th&agrave;nh vi&ecirc;n, đơn vị trực thuộc Đại học Quốc gia H&agrave; Nội.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh tham gia chương tr&igrave;nh, ứng vi&ecirc;n sẽ kh&ocirc;ng được tiếp tục tham gia khi kết quả học tập kh&ocirc;ng đạt từ loại kh&aacute; trở l&ecirc;n hoặc vi phạm quy định của ph&aacute;p luật, Đại học Quốc gia H&agrave; Nội v&agrave; đối t&aacute;c quốc tế.</p>\r\n', '2025-05-23 00:44:17', '../images/1747935857_gd04.jpg', 'Sinh viên Trường đại học Công nghệ, Đại học Quốc gia Hà Nội tìm hiểu thông tin tại ngày hội việc làm năm 2025 - Ảnh: NGUYÊN BẢO', 1, 1),
(38, 1, 1, 'Trình Quốc hội phổ cập giáo dục mầm non cho trẻ 3-5 tuổi', 'Chính phủ chính thức trình Quốc hội hai dự thảo nghị quyết về phổ cập giáo dục mầm non cho trẻ 3-5 tuổi; miễn, hỗ trợ học phí cho học sinh từ năm học 2025-2026.', '<p>S&aacute;ng 22-5,&nbsp;<a href=\"https://tuoitre.vn/bo-truong-nguyen-kim-son-ngan-chan-viec-ep-hoc-sinh-hoc-them-duoi-moi-hinh-thuc-20250516210959847.htm\" target=\"_blank\" title=\"Bộ trưởng Bộ Giáo dục và Đào tạo Nguyễn Kim Sơn \">Bộ trưởng Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Nguyễn Kim Sơn&nbsp;</a>tr&igrave;nh b&agrave;y trước Quốc hội tờ tr&igrave;nh dự thảo nghị quyết của Quốc hội về phổ cập gi&aacute;o dục mầm non cho trẻ mẫu gi&aacute;o từ 3 đến 5 tuổi; nghị quyết của Quốc hội về miễn, hỗ trợ học ph&iacute; đối với trẻ em mầm non, học sinh phổ th&ocirc;ng, người học chương tr&igrave;nh gi&aacute;o dục phổ th&ocirc;ng tại c&aacute;c cơ sở gi&aacute;o dục trong hệ thống gi&aacute;o dục quốc d&acirc;n.</p>\r\n\r\n<p>Nghị quyết phổ cập gi&aacute;o dục, theo &ocirc;ng Sơn, nhằm x&acirc;y dựng h&agrave;nh lang ph&aacute;p l&yacute;, huy động nguồn lực để đảm bảo phổ cập gi&aacute;o dục mầm non chất lượng, chuẩn bị tốt cho trẻ về mọi mặt trước khi v&agrave;o lớp 1, g&oacute;p phần thực hiện quyền trẻ em.</p>\r\n\r\n<p>Nghị quyết &aacute;p dụng đối với người học l&agrave; trẻ em từ 3 đến 5 tuổi trong c&aacute;c cơ sở gi&aacute;o dục mầm non (kh&ocirc;ng bao gồm c&aacute;c cơ sở gi&aacute;o dục mầm non c&oacute; vốn đầu tư nước ngo&agrave;i) v&agrave; c&aacute;c tổ chức, c&aacute; nh&acirc;n li&ecirc;n quan.</p>\r\n\r\n<p>Nghị quyết quy định về mục ti&ecirc;u ho&agrave;n th&agrave;nh&nbsp;<a href=\"https://tuoitre.vn/thu-tuong-huy-dong-nguon-luc-de-pho-cap-giao-duc-mam-non-tu-3-5-tuoi-20240404155359816.htm\" target=\"_blank\" title=\"phổ cập giáo dục mầm non\">phổ cập gi&aacute;o dục mầm non</a>&nbsp;cho trẻ mẫu gi&aacute;o từ 3 đến 5 tuổi tr&ecirc;n phạm vi to&agrave;n quốc v&agrave;o năm 2030.</p>\r\n\r\n<p>Việc phổ cập được thực hiện theo lộ tr&igrave;nh ph&ugrave; hợp với điều kiện ph&aacute;t triển kinh tế - x&atilde; hội của đất nước, bảo đảm c&aacute;c điều kiện phổ cập theo quy định. Huy động nguồn lực của x&atilde; hội đầu tư ph&aacute;t triển gi&aacute;o dục mầm non dưới mọi h&igrave;nh thức theo quy định của ph&aacute;p luật.</p>\r\n\r\n<p>Nghị quyết giao Ch&iacute;nh phủ ban h&agrave;nh cơ chế, ch&iacute;nh s&aacute;ch thực hiện, bao gồm: đầu tư mạng lưới trường lớp, cơ sở vật chất tối thiểu; bảo đảm gi&aacute;o vi&ecirc;n, kinh ph&iacute; cho mầm non c&ocirc;ng lập; v&agrave; bổ sung, sửa đổi ch&iacute;nh s&aacute;ch cho trẻ em, gi&aacute;o vi&ecirc;n, c&aacute;n bộ quản l&yacute; mầm non.</p>\r\n\r\n<p>Ch&iacute;nh s&aacute;ch đột ph&aacute; về đầu tư ph&aacute;t triển gi&aacute;o dục mầm non; ưu ti&ecirc;n ph&aacute;t triển gi&aacute;o dục mầm non ở miền n&uacute;i, v&ugrave;ng đồng b&agrave;o d&acirc;n tộc thiểu số, hải đảo, bi&ecirc;n giới, b&atilde;i ngang ven biển, v&ugrave;ng c&oacute; điều kiện kinh tế - x&atilde; hội kh&oacute; khăn, đặc biệt kh&oacute; khăn, địa b&agrave;n c&oacute; khu c&ocirc;ng nghiệp, khu chế xuất.</p>\r\n\r\n<p>Đồng thời nghi&ecirc;n cứu x&acirc;y dựng, ban h&agrave;nh ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch x&atilde; hội h&oacute;a v&agrave; c&aacute;c ch&iacute;nh s&aacute;ch ph&aacute;t triển gi&aacute;o dục mầm non ph&ugrave; hợp quy định của ph&aacute;p luật.</p>\r\n\r\n<p>Kinh ph&iacute; thực hiện nghị quyết, sau khi được th&ocirc;ng qua, gồm: nguồn lực Ch&iacute;nh phủ bảo đảm phổ cập gi&aacute;o dục mầm non (3-5 tuổi); ng&acirc;n s&aacute;ch nh&agrave; nước bổ sung ngo&agrave;i 20% tổng chi gi&aacute;o dục (theo Luật Gi&aacute;o dục) v&agrave; c&aacute;c nguồn vốn x&atilde; hội h&oacute;a hợp ph&aacute;p kh&aacute;c.</p>\r\n', '2025-05-23 00:45:52', '../images/1747935952_gd05.jpg', 'Bộ trưởng Bộ Giáo dục và Đào tạo Nguyễn Kim Sơn - Ảnh: GIA HÂN', 2, 1),
(39, 1, 1, 'Chắp cánh ước mơ với học bổng ‘Tiếp bước hành trình’', 'Trong hành trình chinh phục tri thức, không phải ai cũng may mắn khởi đầu từ những điều kiện thuận lợi. Nhưng ở đâu có ý chí, ở đó luôn có hy vọng.', '<p>Đ&ocirc;i khi, chỉ cần một c&aacute;nh tay kịp thời đưa ra, một suất học bổng đ&uacute;ng l&uacute;c được trao, cả tương lai của một con người c&oacute; thể rẽ sang một hướng mới tươi s&aacute;ng hơn. Đ&oacute; ch&iacute;nh l&agrave; sứ mệnh m&agrave; học bổng &quot;Tiếp bước h&agrave;nh tr&igrave;nh&quot; của Trường đại học Quốc tế S&agrave;i G&ograve;n (SIU) đ&atilde; v&agrave; đang thực hiện.</p>\r\n\r\n<p>Khởi xướng từ năm 2024, &quot;Tiếp bước h&agrave;nh tr&igrave;nh&quot; mang n&eacute;t đặc biệt ở c&aacute;ch chương tr&igrave;nh được lan tỏa th&ocirc;ng qua ch&iacute;nh cựu học sinh c&aacute;c trường THPT, nay đ&atilde; trở th&agrave;nh sinh vi&ecirc;n của trường.</p>\r\n\r\n<p>Ch&iacute;nh những thế hệ từng được tiếp sức ấy đ&atilde; trở th&agrave;nh cầu nối y&ecirc;u thương, đưa chương tr&igrave;nh học bổng trở về m&aacute;i trường xưa, tiếp tục trao gửi cơ hội cho những thế hệ đ&agrave;n em đang cần được hỗ trợ.</p>\r\n\r\n<p>Phạm Thị Thu Lợi, sinh vi&ecirc;n năm nhất ng&agrave;nh kế to&aacute;n, từng l&agrave; một trong những th&iacute; sinh được học bổng hỗ trợ năm 2024, hiện đang t&iacute;ch cực kết nối học bổng về Trường THPT Vĩnh Long.</p>\r\n\r\n<p>&quot;Đ&atilde; c&oacute; l&uacute;c em nghĩ giấc mơ đại học sẽ m&atilde;i chỉ l&agrave; ước mơ v&igrave; ho&agrave;n cảnh gia đ&igrave;nh kh&ocirc;ng cho ph&eacute;p. Nhưng rồi học bổng đ&atilde; đến như một tia hy vọng, mở ra cho em một con đường mới, một tương lai mới. Nhờ đ&oacute;, em tiếp tục được học tập v&agrave; hơn thế nữa l&agrave; t&igrave;m thấy niềm tin v&agrave;o ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>B&acirc;y giờ, em mong muốn lan tỏa điều kỳ diệu đ&oacute; khi quay trở lại Trường THPT Vĩnh Long - nơi em từng học, để t&igrave;m kiếm v&agrave; gi&uacute;p đỡ thế hệ đ&agrave;n em. Em mong c&aacute;c bạn cũng sẽ được tiếp th&ecirc;m sức mạnh, như em từng được&quot;, Thu Lợi chia sẻ.</p>\r\n\r\n<p>Ngo&agrave;i Thu Lợi, đ&atilde; c&oacute; rất nhiều sinh vi&ecirc;n SIU quay về c&aacute;c trường THPT tại nhiều tỉnh th&agrave;nh tr&ecirc;n cả nước để giới thiệu học bổng v&agrave; hỗ trợ c&aacute;c bạn học sinh l&agrave;m hồ sơ ứng tuyển.</p>\r\n\r\n<p>Năm 2025, trường tiếp tục triển khai chương tr&igrave;nh học bổng &quot;Tiếp bước h&agrave;nh tr&igrave;nh&quot; với 50 suất học bổng to&agrave;n phần, t&agrave;i trợ 100% học ph&iacute; to&agrave;n kh&oacute;a học (trị gi&aacute; 262,17 - 712,63 triệu đồng/suất) kh&ocirc;ng y&ecirc;u cầu điều kiện duy tr&igrave;.</p>\r\n\r\n<p>&quot;Kh&ocirc;ng dừng lại ở những con số, gi&aacute; trị thật sự nằm ở việc mở ra một c&aacute;nh cửa mới cho tương lai của c&aacute;c em - c&aacute;nh cửa dẫn đến tri thức, ước mơ v&agrave; sự đổi thay t&iacute;ch cực trong cuộc sống. Đ&oacute; l&agrave; niềm tin m&agrave; SIU muốn gieo mầm v&agrave; đồng h&agrave;nh c&ugrave;ng c&aacute;c em tr&ecirc;n h&agrave;nh tr&igrave;nh trưởng th&agrave;nh&quot;, đại diện SIU nhấn mạnh về &yacute; nghĩa của học bổng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, trường tiếp tục đồng h&agrave;nh c&ugrave;ng c&aacute;c em suốt 4 năm đại học bằng c&aacute;c chương tr&igrave;nh cố vấn học tập, hỗ trợ t&acirc;m l&yacute;, hướng nghiệp v&agrave; ph&aacute;t triển kỹ năng.</p>\r\n\r\n<p>Nhiều sinh vi&ecirc;n từ chương tr&igrave;nh học bổng hiện đ&atilde; trở th&agrave;nh trưởng nh&oacute;m học tập, thủ lĩnh c&acirc;u lạc bộ v&agrave; đang g&oacute;p phần tạo n&ecirc;n một cộng đồng sinh vi&ecirc;n biết quan t&acirc;m, sẻ chia.</p>\r\n', '2025-05-23 00:47:34', '../images/1747936054_gd06.jpg', 'Thu Lợi truyền động lực bằng chính câu chuyện của mình - Ảnh: SIU', 6, 1),
(40, 2, 8, 'Thủ tướng: Xây dựng trung tâm tài chính quốc tế, đã làm là phải thành công', 'Để tăng trưởng 2 con số thì phải có vốn, trong khi chúng ta đang thiếu vốn. Do đó Thủ tướng Phạm Minh Chính cho rằng phải xây dựng, phát triển trung tâm tài chính quốc tế để bổ sung nguồn lực tài chính.', '<p>Chiều 22-5,&nbsp;<a href=\"https://tuoitre.vn/thu-tuong-pham-minh-chinh-dang-huong-vieng-cac-anh-hung-liet-si-tai-con-dao-20250503130955122.htm\">Thủ tướng Phạm Minh Ch&iacute;nh&nbsp;</a>chủ tr&igrave; cuộc họp tiếp tục thảo luận, cho &yacute; kiến với dự thảo nghị quyết của Quốc hội về x&acirc;y dựng trung t&acirc;m t&agrave;i ch&iacute;nh quốc tế tại Việt Nam.</p>\r\n\r\n<p>Tại cuộc họp, c&aacute;c đại biểu đ&atilde; tiếp tục cho &yacute; kiến v&agrave;o dự thảo nghị quyết của Quốc hội - đ&acirc;y l&agrave; dự thảo lần thứ 29 sau nhiều sửa đổi, bổ sung.</p>\r\n\r\n<p>C&aacute;c vấn đề được tập trung thảo luận tại cuộc họp bao gồm: mục ti&ecirc;u của việc x&acirc;y dựng trung t&acirc;m t&agrave;i ch&iacute;nh quốc tế; m&ocirc; h&igrave;nh trung t&acirc;m t&agrave;i ch&iacute;nh quốc tế tại Việt Nam; c&aacute;c định hướng ph&aacute;t triển v&agrave; lĩnh vực trọng t&acirc;m; th&agrave;nh vi&ecirc;n, c&aacute;c sản phẩm chủ yếu của&nbsp;<a href=\"https://tuoitre.vn/trung-tam-tai-chinh-quoc-te-tp-hcm-can-xay-dung-he-sinh-thai-hoan-chinh-20250422235459555.htm\">trung t&acirc;m t&agrave;i ch&iacute;nh</a>; c&aacute;c ch&iacute;nh s&aacute;ch, cơ chế đặc th&ugrave;; cơ sở hạ tầng, nguồn nh&acirc;n lực v&agrave; thị thực; việc &aacute;p dụng thể chế, th&ocirc;ng lệ quốc tế; vấn đề tự do h&oacute;a ngoại hối v&agrave; hoạt động ng&acirc;n h&agrave;ng; việc giải quyết tranh chấp theo chuẩn mực quốc tế&hellip;</p>\r\n\r\n<p>Ph&aacute;t biểu kết luận, Thủ tướng Phạm Minh Ch&iacute;nh đ&aacute;nh gi&aacute; cao việc chuẩn bị của cơ quan chủ tr&igrave; x&acirc;y dựng dự thảo, c&aacute;c &yacute; kiến t&acirc;m huyết, tr&aacute;ch nhiệm, chất lượng; y&ecirc;u cầu tiếp thu tối đa c&aacute;c &yacute; kiến, tiếp tục ho&agrave;n thiện th&ecirc;m một bước dự thảo nghị quyết.</p>\r\n\r\n<p>Tinh thần l&agrave; vừa l&agrave;m vừa r&uacute;t kinh nghiệm, mở rộng dần, kh&ocirc;ng qu&aacute; cầu to&agrave;n, kh&ocirc;ng n&oacute;ng vội; đ&atilde; l&agrave;m l&agrave; phải th&agrave;nh c&ocirc;ng, phải thắng lợi, l&agrave;m tới đ&acirc;u chắc tới đ&oacute;, theo người đứng đầu Ch&iacute;nh phủ.</p>\r\n\r\n<p>Thủ tướng cũng n&ecirc;u r&otilde; Việt Nam đ&atilde; x&aacute;c định 2 mục ti&ecirc;u 100 năm (đến năm 2030 l&agrave; nước đang ph&aacute;t triển c&oacute; c&ocirc;ng nghiệp hiện đại, thu nhập trung b&igrave;nh cao; đến năm 2045 trở th&agrave;nh nước ph&aacute;t triển, thu nhập cao). Muốn đạt được th&igrave; phải tăng trưởng kinh tế ở mức 2 con số, tạo m&ocirc;i trường thuận lợi, Thủ tướng nhấn mạnh.</p>\r\n\r\n<p>Song để tăng trưởng 2 con số th&igrave; phải c&oacute; vốn, trong khi ch&uacute;ng ta đang thiếu vốn. Do đ&oacute; Thủ tướng cho rằng phải x&acirc;y dựng, ph&aacute;t triển trung t&acirc;m t&agrave;i ch&iacute;nh quốc tế để bổ sung nguồn lực t&agrave;i ch&iacute;nh cho 2 mục ti&ecirc;u 100 năm.</p>\r\n', '2025-05-23 00:57:57', '../images/1747936677_kt1.jpg', 'Thủ tướng Phạm Minh Chính lưu ý tự do hóa các hoạt động kinh doanh nhưng có lộ trình phù hợp với nền kinh tế - Ảnh: VGP', 2, 1),
(41, 5, 1, 'TP.HCM 1 áp đảo, vô địch đồng đội nữ Giải bóng bàn quốc gia', 'TP.HCM 1 đã có được tấm huy chương vàng đầu tiên tại Giải bóng bàn vô địch quốc gia 2025, khi chiến thắng nội dung đồng đội nữ tối 22-5.', '<p>Trong nhiều năm qua, c&aacute;c tay vợt nữ của&nbsp;<a href=\"https://tuoitre.vn/ngon-lua-bong-ban-50-nam-van-chay-20250421084149581.htm\">b&oacute;ng b&agrave;n</a>&nbsp;TP.HCM lu&ocirc;n thể hiện được đẳng cấp vượt trội, lấn lướt trước c&aacute;c đối thủ. Do đ&oacute;, họ được xem l&agrave; ứng vi&ecirc;n số 1 cho chức v&ocirc; địch năm nay.</p>\r\n\r\n<p>Thực tế tr&ecirc;n h&agrave;nh tr&igrave;nh v&agrave;o đến chung kết, TP.HCM 1 kh&ocirc;ng gặp qu&aacute; nhiều kh&oacute; khăn. Ba VĐV gồm Nguyễn Khoa Diệu Kh&aacute;nh, Mai Ho&agrave;ng Mỹ Trang v&agrave; Nguyễn Bạch Thanh Thư tỏ ra &aacute;p đảo trước c&aacute;c đối thủ.</p>\r\n\r\n<p>Tại b&aacute;n kết, họ gi&agrave;nh chiến thắng dễ d&agrave;ng 3-0 trước Qu&acirc;n Đội 2 để điền t&ecirc;n v&agrave;o trận tranh chức v&ocirc; địch.</p>\r\n', '2025-05-23 07:21:59', '../images/1747959719_anh2.jpg', 'Phòng thí nghiệm phục vụ đào tạo và nghiên cứu về bán dẫn của trường Đại học Khoa học Tự nhiên, Đại học Quốc gia Hà Nội. Ảnh: Giang Huy', 6, 1),
(44, 1, 12, 'Nơi kết thúc bắt đầu: Chọn quên hết hay nhớ thật nhiều?', 'gikgdyhdgf', '<p>dgshfgdgfj s&aacute;dfgftghfg d&agrave;gsdg</p>\r\n', '2025-05-23 08:47:59', '../images/1747964879_anh1.jpg', 'àdadsgdfsaasd', 6, 1);

